export const i18nReady = initPromise;
export default i18n;
// Get supported cultures from local definitions
export function getSupportedCultures() {
    return Object.entries(CULTURE_NAMES).map(([code, displayName]) => ({
        code,
        displayName
    }));
}
// Set culture locally only
export async function setCulture(cultureCode) {
    await i18n.changeLanguage(cultureCode);
    setRTLDirection(cultureCode);
}
// Note: CSRF token function removed as it's not used in local-only mode
// Set RTL direction based on language
export function setRTLDirection(languageCode) {
    const htmlElement = document.documentElement;
    const isRTL = RTL_LANGUAGES.some(rtlLang => languageCode.toLowerCase().startsWith(rtlLang.toLowerCase()));
    htmlElement.setAttribute('dir', isRTL ? 'rtl' : 'ltr');
    htmlElement.setAttribute('lang', languageCode);
}
// Check if current language is RTL
export function isRTL() {
    const currentLang = i18n.language;
    return RTL_LANGUAGES.some(rtlLang => currentLang.toLowerCase().startsWith(rtlLang.toLowerCase()));
}
// Hook for using translations with namespace support
export function useT(namespace) {
    const { t } = useTranslation(namespace);
    return t;
}
